tinymce.PluginManager.add('ooccdimage', function(editor, url) {
  // Add a button that opens a chemdoodle window

  editor.addButton('ooccdimage', {
    text: '2D',
    tooltip: 'Draw and insert 2D structure',
    icon: false,
    onclick: function() {
      // Open window
      var win = editor.windowManager.open({
        title: 'Draw structure below',
		file : url + '/dialog.html',
		
		width: 630,
        height: 410,
        onsubmit: function(e) {

        }
      });
    }
  });


});
